//Obtener desde el contexto flow
var mensajesProcesados=flow.get("mensajesProcesados");
var mensajesDescartados=flow.get("mensajesDescartados");
if(!mensajesProcesados)
    mensajesProcesados=0;
if(!mensajesDescartados)
    mensajesDescartados=0;

var l=msg.payload.trim();
msg.payload=false;
if(l!=="") {
    mensajesProcesados++;
    var p=l.split(" ");
    msg.payload=p;
    if(p[0]!="PID") {
        msg.payload={
            "pid": p[0],
            "procname": p[4]
        };
    }
} else {
    mensajesDescartados++;
}

//Almacenar en el contexto flow
flow.set("mensajesProcesados",mensajesProcesados);
flow.set("mensajesDescartados",mensajesDescartados);

global.set("totalMensajes",mensajesProcesados+mensajesDescartados);

return msg;

if(msg.payload>=0 && msg.payload<=33) {
    return [msg,null,null,null];
} else if(msg.payload>33 && msg.payload<=66) {
    return [null,msg,null,null];
} else if(msg.payload>66 && msg.payload<=100) {
    return [null,null,msg,null]; 
} else {
    return [null,null,null,msg];
}

var authToken=msg.req.headers['x-auth-token'];
msg.auth= (authToken && authToken=='clave');
if(!msg.auth) {
    msg.statusCode=401;
}
msg.cabeceras=[];

Object.keys(msg.req.headers).forEach(function(o,i)  {
    msg.cabeceras.push({name:o, value:msg.req.headers[o]});
});
return msg;
